package com.jugru.binaryTree;public class Node {    public Node(long value, Node parent) {        this.value = value;        this.parent = parent;    }    public Node(long value) {        this.value = value;    }    private long value;    private Node parent;    private Node left;    private Node right;    public long getValue() {        return value;    }    public Node getParent() {        return parent;    }    public Node getLeft() {        return left;    }    public Node getRight() {        return right;    }    @Override    public String toString() {        return "Node{" +                "value=" + value +                '}';    }    public void setParent(Node parent) {        this.parent = parent;    }    public void setLeft(Node left) {        this.left = left;    }    public void setRight(Node right) {        this.right = right;    }}